import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import {
  getStorage,
  ref,
  uploadBytesResumable,
  getDownloadURL,
} from 'firebase/storage';

import { useAuth } from '../../contexts/auth-contexts';
import { toast } from 'react-toastify';

const GiveDogs = () => {
  const [imgDogs, setImgDogs] = useState();
  useEffect(() => {
    document.title = 'GiveDogs | Paradise for pets';
  }, []);

  const handleUploadImg = (e) => {
    const files = e.target.files;
    if (!files) return;
    const forEachFile = files.forEach((file) => file);
    const storage = getStorage();
    const storageRef = ref(storage, 'images/' + files.name);
    const uploadTask = uploadBytesResumable(storageRef, files);

    uploadTask.on(
      'state_changed',
      (snapshot) => {
        const progress =
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log('Upload is ' + progress + '% done');
        switch (snapshot.state) {
          case 'paused':
            console.log('Upload is paused');
            break;
          case 'running':
            console.log('Upload is running');
            break;
          default:
            console.log('empty');
        }
      },
      (error) => {
        toast.error('Có lỗi xảy ra');
      },
      () => {
        // Upload completed successfully, now we can get the download URL
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          console.log('File available at', downloadURL);
        });
      }
    );
  };

  const { userInfo } = useAuth();

  return (
    <>
      {userInfo ? (
        <>
          <input type='file' multiple onChange={handleUploadImg} />
          {imgDogs && <img src={imgDogs.preview} alt={'dogs'} />}
        </>
      ) : (
        <>
          <div className='flex flex-col items-center justify-center mx-auto mt-14 gap-y-6'>
            <p className='text-3xl font-semibold'>
              Chức năng này chỉ khả dụng khi bạn đã đăng nhập!
            </p>
            <div className='text-xl'>
              <span>
                <Link className='mr-2 text-blue-500 ' to={'/sign-in'}>
                  Đăng nhập
                </Link>{' '}
                tại đây nhé
              </span>

              <p className='mt-5'>
                Nếu bạn chưa có tài khoản hãy
                <Link className='mx-2 text-blue-500' to={'/sign-up'}>
                  Đăng ký
                </Link>{' '}
                1 tài khoản nhé
              </p>
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default GiveDogs;
